import React, { useState } from 'react';

import { fetchWeather } from './api/fetchWeather';
import './App.css';

const App = () => {
    const [query, setQuery] = useState('');
    const [weather, setWeather] = useState({}); //we are setting the weather to the data we recieve from API

    const search = async (e) => {
        if(e.key === 'Enter') { //once the search is done this will run
            const data = await fetchWeather(query); //we will fetch the weather data based of the search

            setWeather(data);
            setQuery(''); //this will reset the query
        }
    }

    return (
        <div className="main-container">
            <input //search bar
                type="text"
                className="search"
                placeholder="Search..."
                value={query}
                onChange={(e) => setQuery(e.target.value)}onKeyPress={search}
            />
            {weather.main && ( 
                <div className="city">
                    <h2 className="city-name">
                        <span>{weather.name}</span>
                        <sup>{weather.sys.country}</sup>
                    </h2>
                    <div className="city-temp">
                        {Math.round(weather.main.temp)}
                        <sup>&deg;C</sup>
                    </div>
                    <div className="city-feels-like">
                        <span>Feels like: </span>
                        {Math.round(weather.main.feels_like)}
                        <sup>&deg;C</sup>
                    </div>
                    <div className="city-sunrise">
                        <span>Sunrise: </span>
                        {Math.round(weather.main.sunrise)}
                        <span>&deg;AM</span>
                    </div>
                    <div className="city-sunset">
                        <span>Sunset: </span>
                        {Math.round(weather.main.sunset)}
                        <span>&deg; PM </span>
                    </div>
                    <div className="info">
                        <img className="city-icon" src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`} alt={weather.weather[0].description} />
                        <p>{weather.weather[0].description}</p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
